import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Product } from '../models/product';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {

  @Input()
  product!: Product;

  @Output() hoveredEvent = new EventEmitter<string>();

  constructor() { }

  ngOnInit(): void {
  }

  hovered(name: string, flag: boolean) {
    let event = JSON.stringify({name: name, flag: flag});
    this.hoveredEvent.emit(event);
  }

  addToCart(product: Product) {
    let cart = [];

    cart = JSON.parse(localStorage.getItem('cart') || `[]`);

    console.log(cart);

    let item = cart.find((item: any) => item.id === product.id);
    if (item) {
      item.name = product.name;
      item.quantity += 1;
      item.price = product.price;
    } else {
      cart.push({
        id: product.id,
        name: product.name,
        quantity: 1,
        price: product.price,
      });
    }

    console.table(cart);

    localStorage.setItem('cart', JSON.stringify(cart));
  }

  removeFromCart(product: Product) {
    let cart = [];
    cart = JSON.parse(localStorage.getItem('cart') || `[]`);

    let item = cart.find((item: any) => item.id === product.id);
    if (item) {
      item.quantity -= 1;
      if (item.quantity <= 0) {
        cart = cart.filter((item: any) => item.id !== product.id);
      }
    }

    console.table(cart);

    localStorage.setItem('cart', JSON.stringify(cart));
  }
}
<div (mouseenter)="hovered(product.name, true)" (mouseleave)="hovered(product.name, false)">
    <h3>
        <a [title]="product.name + ' details'">
            {{ product.name }}
        </a>
    </h3>

    <img src="{{product.image}}" alt="{{product.name}}" />

    <p *ngIf="product.description">
        {{ product.description }}
    </p>

    <button (click)="addToCart(product)">Add to cart</button>

    <button (click)="removeFromCart(product)">Remove from cart</button>
</div>
